    private void jButtonParesActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonParesActionPerformed
        int i;
        DefaultComboBoxModel modelo = new DefaultComboBoxModel();
        
        for (i = 0; i < 10; i+=2) {
            modelo.addElement(i);
        }
        
        this.jComboBoxNumeros.setModel(modelo);
    }//GEN-LAST:event_jButtonParesActionPerformed

    private void jButtonImparesActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonImparesActionPerformed
        int i;
        DefaultComboBoxModel modelo = new DefaultComboBoxModel();
        
        for (i = 1; i < 10; i+=2) {
            modelo.addElement(i);
        }
        
        this.jComboBoxNumeros.setModel(modelo);
    }//GEN-LAST:event_jButtonImparesActionPerformed

